import { useState, useEffect } from 'react'
import { Route, Routes, Link, useMatch, useNavigate } from 'react-router-dom'
import { useField } from './hooks'

const Menu = () => {
	const padding = {
		paddingRight: 5,
	}
	return (
		<div>
			<Link to='/' style={padding}>
				anecdotes
			</Link>
			<Link to='/create' style={padding}>
				create new
			</Link>
			<Link to='/about' style={padding}>
				about
			</Link>
		</div>
	)
}

const AnecdoteList = ({ anecdotes }) => (
	<div>
		<h2>Anecdotes</h2>
		<ul>
			{anecdotes.map((anecdote) => (
				<li key={anecdote.id}>
					<Link to={`/anecdotes/${anecdote.id}`}>
						{anecdote.content}
					</Link>
				</li>
				// <li key={anecdote.id}>{anecdote.content}</li>
			))}
		</ul>
	</div>
)

const Anecdote = ({ anecdote }) => (
	<div>
		<h2>{anecdote.content}</h2>
		<p>Written by: {anecdote.author}</p>
		<p>
			For more info, visit:{' '}
			<a href={anecdote.info} target='blank'>
				{anecdote.info}
			</a>
		</p>
	</div>
)

const About = () => (
	<div>
		<h2>About anecdote app</h2>
		<p>According to Wikipedia:</p>

		<em>
			An anecdote is a brief, revealing account of an individual person or
			an incident. Occasionally humorous, anecdotes differ from jokes
			because their primary purpose is not simply to provoke laughter but
			to reveal a truth more general than the brief tale itself, such as
			to characterize a person by delineating a specific quirk or trait,
			to communicate an abstract idea about a person, place, or thing
			through the concrete details of a short narrative. An anecdote is "a
			story with a point."
		</em>

		<p>
			Software engineering is full of excellent anecdotes, at this app you
			can find the best and add more.
		</p>
	</div>
)

const Footer = () => (
	<div>
		Anecdote app for{' '}
		<a href='https://fullstackopen.com/'>Full Stack Open</a>. See{' '}
		<a href='https://github.com/fullstack-hy2020/routed-anecdotes/blob/master/src/App.js'>
			https://github.com/fullstack-hy2020/routed-anecdotes/blob/master/src/App.js
		</a>{' '}
		for the source code.
	</div>
)

const CreateNew = (props) => {
	const content = useField('text')
	const author = useField('text')
	const info = useField('text')

	const contentProps = { resetValue, ...content }
	const authorProps = { resetValue, ...author }
	const infoProps = { resetValue, ...info }

	const handleSubmit = (e) => {
		e.preventDefault()
		props.addNew({
			content: content.value,
			author: author.value,
			info: author.value,
			votes: 0,
		})
	}

	return (
		<div>
			<h2>create a new anecdote</h2>
			<form onSubmit={handleSubmit}>
				<div>
					content
					<input {...contentProps} />
				</div>
				<div>
					author
					<input {...authorProps} />
				</div>
				<div>
					url for more info
					<input {...infoProps} />
				</div>
				<button type='submit'>create</button>
				<button
					onClick={(e) => {
						e.preventDefault()
						content.resetValue()
						author.resetValue()
						info.resetValue()
					}}
				>
					reset
				</button>
			</form>
		</div>
	)
}

const App = () => {
	const [anecdotes, setAnecdotes] = useState([
		{
			content: 'If it hurts, do it more often',
			author: 'Jez Humble',
			info: 'https://martinfowler.com/bliki/FrequencyReducesDifficulty.html',
			votes: 0,
			id: 1,
		},
		{
			content: 'Premature optimization is the root of all evil',
			author: 'Donald Knuth',
			info: 'http://wiki.c2.com/?PrematureOptimization',
			votes: 0,
			id: 2,
		},
	])

	const [notification, setNotification] = useState(null)

	const navigate = useNavigate()

	useEffect(() => {
		if (notification) {
			setTimeout(() => {
				setNotification(null)
			}, 5000)
		}
	}, [notification])

	const addNew = (anecdote) => {
		console.log('new anecdote:', anecdote)
		anecdote.id = Math.round(Math.random() * 10000)
		setAnecdotes(anecdotes.concat(anecdote))
		setNotification(`A new note '${anecdote.content}' was added!`)
		console.log('created')
		navigate('/')
	}

	const anecdoteById = (id) => anecdotes.find((a) => a.id === id)

	const vote = (id) => {
		const anecdote = anecdoteById(id)

		const voted = {
			...anecdote,
			votes: anecdote.votes + 1,
		}

		setAnecdotes(anecdotes.map((a) => (a.id === id ? voted : a)))
	}

	const matchAnecdote = useMatch('/anecdotes/:id')
	const anecdote = matchAnecdote
		? anecdotes.find((a) => a.id === Number(matchAnecdote.params.id))
		: null

	return (
		<div>
			<h1>Software anecdotes</h1>
			<p>{notification}</p>
			<Menu />
			<Routes>
				<Route
					path='/'
					element={<AnecdoteList anecdotes={anecdotes} />}
				/>
				<Route
					path='/anecdotes/:id'
					element={<Anecdote anecdote={anecdote} />}
				/>
				<Route path='/about' element={<About />} />
				<Route path='/create' element={<CreateNew addNew={addNew} />} />
			</Routes>
			<Footer />
		</div>
	)
}

export default App
